installationScript.script:
hal.do3();

var timeToThrowError = new Date().getTime() + 10 * 1000;
while (true) {
    hal.do1();
    sleep(1000);
    hal.do2();
    sleep(2000);
    if (new Date().getTime() > timeToThrowError) {
        throw new Error("Any error while script running.");
    }
}

function sleep(milliSeconds) {
    var startTime = new Date().getTime();
    while (new Date().getTime() < startTime + milliSeconds);
}
